async function (searchString) {
  let selectedPositionIndex = appsmith.store.currentPositionIndex;
  let configData = appsmith.store.currentConfigState;
  let hardwareConfig = configData.authorizeMatrix[selectedPositionIndex].Hardware;
  let softwareConfig = configData.authorizeMatrix[selectedPositionIndex].Software;
  let licenseConfig = configData.authorizeMatrix[selectedPositionIndex].License;
  let filteredHardwareConfig = await PageLogic.SearchSourceObject(hardwareConfig, searchString);
  let filteredSoftwareConfig = await PageLogic.SearchSourceObject(softwareConfig, searchString);
  let filteredLicenseConfig = await PageLogic.SearchSourceObject(licenseConfig, searchString);
  console.log(filteredHardwareConfig);
  console.log(filteredSoftwareConfig);
  console.log(filteredLicenseConfig);
  await storeValue("hardwareConfig", filteredHardwareConfig, true);
  await storeValue("softwareConfig", filteredSoftwareConfig, true);
  await storeValue("licenseConfig", filteredLicenseConfig, true);
}
