async () => {
  let DataLicense = appsmith.store.Authorize_Data_API_License_NTBX || [];
  if (DataLicense?.length > 0) {
    DataLicense = [];
  }
  await API_GET_Hardwa_NTBX.run();
  await storeValue('Data_API_License_NTBX', API_GET_Hardware_NTBX.data);
  await storeValue('Authorize_Data_API_License_NTBX', undefined);
  for (let i = 0; i < API_GET_Hardware_NTBX.data.length; i++) {
    if (API_GET_Hardware_NTBX.data[i].version_id === 1) {
      DataLicense?.push(API_GET_Hardware_NTBX.data[i]);
      storeValue('Authorize_Data_API_License_NTBX', _.unionBy(DataLicense));
    } else {
      console.log('not found!!');
    }
  }
}
