async () => {
  let DataHardware = appsmith.store.Authorize_Data_API_Hardware_NTBX || [];
  if (DataHardware?.length > 0) {
    DataHardware = [];
  }
  await API_GET_Hardware_NTBX.run();
  await storeValue('Data_API_Hardware_NTBX', API_GET_Hardware_NTBX.data);
  await storeValue('Authorize_Data_API_Hardware_NTBX', undefined);
  for (let i = 0; i < API_GET_Hardware_NTBX.data.length; i++) {
    if (API_GET_Hardware_NTBX.data[i].version_id === 1) {
      DataHardware?.push(API_GET_Hardware_NTBX.data[i]);
      storeValue('Authorize_Data_API_Hardware_NTBX', _.unionBy(DataHardware));
    } else {
      console.log('not found!!');
    }
  }
}
