async (rowName, rowId, rowUpdate) => {
  await storeValue('rowUpdate', rowUpdate);
  await storeValue('rowId', rowId);
  if (rowUpdate === true) {
    const result = "{" + rowName + "false" + "}";
    storeValue('modalUpdateHardware', result);
    await API_PUT_HardwareById.run();
    await JS_getHardware.getHardware();
  } else {
    const result = "{" + rowName + "true" + "}";
    storeValue('modalUpdateHardware', result);
    await API_PUT_HardwareById.run();
    await JS_getHardware.getHardware();
  }
}
